package jossc.game.top;

import cn.nukkit.Player;
import cn.nukkit.Server;
import cn.nukkit.entity.data.Skin;
import cn.nukkit.level.Position;
import cn.nukkit.utils.TextFormat;
import java.util.Base64;
import java.util.List;
import java.util.Random;
import lombok.Getter;
import lombok.RequiredArgsConstructor;
import org.citizen.entity.Citizen;

@RequiredArgsConstructor
@Getter
public class Top {

  private final int ranking;
  private final Player winner;
  private final Position pedestalPosition;
  private Citizen citizen;
  private final List<String> emotes;

  private static final String SKIN_ID =
    "";

  public void spawnEntity() {
    citizen = new Citizen();
    citizen.setPitch(0);
    citizen.setYaw(0);
    citizen.setPosition(pedestalPosition);

    Skin skin = new Skin();
    skin.setSkinId("Custom_Slim");
    skin.setSkinData(Base64.getDecoder().decode(SKIN_ID));

    citizen.setSkin((winner == null ? skin : winner.getSkin()));
    citizen
      .getTagEditor()
      .putLine(
        TextFormat.colorize("&e#" + ranking + "&r&f ") +
        (winner == null ? "None" : winner.getName())
      );

    Random rand = new Random();

    citizen.executeEmote(emotes.get(rand.nextInt(emotes.size())), true, 15);
    Server
      .getInstance()
      .getOnlinePlayers()
      .values()
      .forEach(player -> citizen.spawnTo(player));
  }

  public void despawnEntity() {
    Server
      .getInstance()
      .getOnlinePlayers()
      .values()
      .forEach(player -> citizen.despairFrom(player));
  }
}
